import React, { useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  useLocation,
} from "react-router-dom";
import withRouter from "../hooks/withRouter";
import AppRoutes from "./routes";
import Headermain from "../header";
import AnimatedCursor from "../hooks/AnimatedCursor";
import Home from "../pages/home/index"; // 홈 페이지
import Portfolio from "../pages/portfolio/index"; // 포트폴리오 홈 페이지
import RootProject from "../pages/portfolio/project/rootApp"; // root 프로젝트 페이지
import "./App.css";
import "../style.css";


function _ScrollToTop(props) {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);
  return props.children;
}

const ScrollToTop = withRouter(_ScrollToTop);

export default function App() {
  return (
    <Router basename={process.env.PUBLIC_URL}>
      <div className="cursor__dot">
        <AnimatedCursor
          innerSize={15}
          outerSize={15}
          color="255, 255 ,255"
          outerAlpha={0.4}
          innerScale={0.7}
          outerScale={5}
        />
      </div>
      <ScrollToTop>
        <Headermain />
        <Routes>
          <Route path="/" element={<Home />} /> {/* 홈 페이지로 랜딩 */}
          <Route path="/portfolio" element={<Portfolio />} /> {/* 포트폴리오 홈 페이지 */}
          <Route path="/project/rootApp" element={<RootProject />} /> {/* root 프로젝트 페이지 */}
          <Route path="/*" element={<AppRoutes />} /> {/* 기타 라우트 */}
        </Routes>
      </ScrollToTop>
    </Router>
  );
}
